bool ldpc::decode_hard(const unsigned char *r,unsigned char *u)
{
	unsigned short I = 0; //numero de iteraçoes
	unsigned short aux_S = 0; //auxiliar para saber se a sindrome é zero
	unsigned short c_node = 0; // bit que o check node acredita estar correto

	bool status;

	//copiar entrada
	for(unsigned short i = 0; i < n; i++)
	{
		r_aux[i] = r[i];
	}

	//buscar e corrigir o erro
	while(I < max_it)
	{
		aux_S = 0;

		//zerar sindrome
		for(unsigned short i = 0; i < (n-k); i++)
		{
			SIN[i] = 0;
		}

		//calculo da sindrome
		for(unsigned short i = 0; i < (n-k); i++)
		{
			for(unsigned short j = 0; j < INDX[i]; j++)
			{
				SIN[i] = SIN[i] ^ r_aux[C[i][j]];
			}
		}

		//checar se sindrome � zero
		for(unsigned short i = 0; i < (n-k); i++)
		{
			aux_S = aux_S + SIN[i];    
		}

		// mensagem ok
		if(aux_S == 0) 
		{
			//printf("MENSAGEM OK. ITERACOES = %i\n", I);
			status = true;
			goto end;
		}
		else
		{
			I++;

			printf("ITERACAO %i\n", I);

			//zerar err
			for(unsigned short i = 0; i < n; i++)
			{
				err[i][0] = 0;
				err[i][1] = 0;
			}

			for(unsigned short i = 0; i < (n-k); i++) // cada fi (check node)
			{
				for(unsigned short j = 0; j < INDX[i]; j++) //cada cj (entrada ligada ao check node)
				{
					c_node = 0;
					for(unsigned short K = 0; K < INDX[i]; K++) //cada cK (entrada ligada ao check node, exceto cj)
					{
						if(K != j)
						{
							c_node = c_node^r_aux[C[i][K]];
						}
					}

					//contar 0s e 1s sugeridos
					if(c_node == 0)
						err[C[i][j]][0]++;
					else if(c_node == 1)
						err[C[i][j]][1]++;
				}
				if(i%100 == 0)
					printf("f%i, INDX[i] = %i\n", i, INDX[i]);
			}

			for(unsigned short i = 0; i < n; i++)
			{
				//contar 0s e 1s na palavra codigo

				if(r_aux[i] == 0)
					err[i][0]++;
				else if(r_aux[i] == 1)
					err[i][1]++;


				//corrigir a palavra codigo

				if(err[i][0] > err[i][1])
				{
					if(r_aux[i] == 1)
						printf("%i INVERTEU DE 1 PARA 0 | 0 = %i | 1 = %i\n", i, err[i][0], err[i][1]);
					r_aux[i] = 0;
				}
				else if(err[i][0] < err[i][1])
				{
					if(r_aux[i] == 0)
						printf("%i INVERTEU DE 0 PARA 1 | 0 = %i | 1 = %i\n", i, err[i][0], err[i][1]);
					r_aux[i] = 1;
				}
			}
		}

		if(I == max_it) //sair do loop se chegar ao max de iteracoes ou chegar em um "deadlock" 
		{
			printf("MENSAGEM NAO CORRIGIDA. ITERACOES = %i\n", I);
			status = false;
			goto end;
		}
	}

end:

	//printf("ITERACOES = %i\n ", I);

	//r_aux é a mensagem corrigida, u é a saída, sem os bits de paridade
	for(unsigned short i = 0; i < k; i++)
	{
		u[i] = r_aux[i];
	}

	if(status)
		return true;
	else
		return false;
}

